import React, {forwardRef, useContext, useEffect, useImperativeHandle} from 'react';
import {useTranslation} from 'react-i18next';

import {Box, Grid, Stack, Typography} from '@mui/material';

import {EsLibraryCard} from '../../../shared/components/esLibrarySelector';
import {LibrariesContext, LibrariesContextProvider} from '../../../shared/context/library';
import {WizardContext} from '../../../shared/context/wizard/WizardState';
import {Library} from '../../../shared/models/library';
import {fetchLibraries as fetchMovieLibraries} from '../../../shared/services/movieService';
import {fetchLibraries as fetchShowLibraries} from '../../../shared/services/showService';
import {LibraryStepProps, ValidationHandle} from '../Interfaces';

export const ConfigureLibrary =
forwardRef<ValidationHandle, LibraryStepProps>((props, ref) => {
  // eslint-disable-next-line react/prop-types
  const {type} = props;
  const {setMovieLibraryIds, setShowLibraryIds} = useContext(WizardContext);
  if (type === 'movie') {
    return (
      <LibrariesContextProvider>
        <ConfigureLibraryContainer
          fetch={fetchMovieLibraries}
          type="COMMON.MOVIE"
          push={setMovieLibraryIds}
          ref={ref}
        />
      </LibrariesContextProvider>
    );
  }

  return (
    <LibrariesContextProvider>
      <ConfigureLibraryContainer
        fetch={fetchShowLibraries}
        type="COMMON.SHOW"
        push={setShowLibraryIds}
        ref={ref} />
    </LibrariesContextProvider>
  );
});

type Props = {
  fetch: () => Promise<Library[]>;
  type: 'COMMON.SHOW' | 'COMMON.MOVIE';
  push: (libraryIds: string[]) => Promise<void>;
}

export const ConfigureLibraryContainer =
forwardRef<ValidationHandle, Props>((props, ref) => {
  // eslint-disable-next-line react/prop-types
  const {fetch, type, push} = props;
  const {t} = useTranslation();
  const {load, libraries, save} = useContext(LibrariesContext);
  const {wizard} = useContext(WizardContext);

  useEffect(() => {
    (async () => {
      load(fetch);
    })();
  }, []);

  useImperativeHandle(ref, () => ({
    async validate(): Promise<boolean> {
      await save(push);
      return Promise.resolve(true);
    },
  }));

  return (
    <Stack spacing={2}>
      <Typography variant="h4" color="primary">
        {t('WIZARD.LIBRARIES.TITLE', {type: t(type)})}
      </Typography>
      <Typography variant="body1">
        {t('SETTINGS.LIBRARIES.CONTENT', {type: t(type)})}
      </Typography>
      <Box>
        <Grid container={true} spacing={1}>
          {
            libraries.map((lib) => (
              <EsLibraryCard
                key={lib.id}
                library={lib}
                fallbackServerUrl={wizard.address}
              />))
          }
        </Grid>
      </Box>
    </Stack>
  );
});

import React, {forwardRef, useContext, useEffect, useImperativeHandle} from 'react';
import {Controller, useForm} from 'react-hook-form';
import {useTranslation} from 'react-i18next';

import {
  Box, Card, CardContent, FormControl, Grid, MenuItem, Select, Typography,
} from '@mui/material';

import {EsTextInput} from '../../../../shared/components/esTextInput';
import {WizardContext} from '../../../../shared/context/wizard/WizardState';
import {ValidationHandleWithSave} from '../../Interfaces';

export const TestSuccessFul = forwardRef<ValidationHandleWithSave>((props, ref) => {
  const {wizard, setMediaServerNetworkInfo, setAdministrator} = useContext(WizardContext);
  const {t} = useTranslation();

  useImperativeHandle(ref, () => ({
    async validate(): Promise<boolean> {
      const {address, selectedAddress} = getValues();

      if (selectedAddress !== 'other') {
        return Promise.resolve(true);
      }

      await trigger();
      const addressArray = (address).split('://');
      if (isValid) {
        if (!['http', 'https'].includes(addressArray[0])) {
          setError('address', {type: 'protocol'});
          return Promise.resolve(false);
        }

        if (addressArray[1].split(':')[0].length === 0) {
          setError('address', {type: 'url'});
          return Promise.resolve(false);
        }

        if (addressArray[1].split(':')[1].split('/')[0] === undefined ||
          isNaN(parseInt(addressArray[1].split(':')[1].split('/')[0], 10))) {
          setError('address', {type: 'port'});
          return Promise.resolve(false);
        }
      }

      return Promise.resolve(true);
    },
    saveChanges(): void {
      const {address, selectedAdmin} = getValues();

      setMediaServerNetworkInfo({
        address: selectedAddress !== 'other' ? wizard.address : address,
        apiKey: wizard.apiKey,
        id: wizard.serverId,
        name: wizard.serverName,
        type: wizard.serverType,
      });

      const admin = wizard.administrators.find((x) => x.id === selectedAdmin);
      if (admin !== undefined) {
        setAdministrator(admin.id);
      }
    },
  }));

  const {register, getValues, trigger, watch, control, setError,
    clearErrors, formState: {isValid, errors}} = useForm({
    mode: 'onBlur',
    defaultValues: {
      address: '',
      selectedAddress: wizard.address,
      selectedAdmin: wizard.administrators[0].id,
    },
  });

  const selectedAddress = watch('selectedAddress');
  const addressRegister = register('address', {
    validate:
      (value: string) => selectedAddress !== 'other' || value.length > 0,
  });

  useEffect(() => {
    clearErrors('address');
  }, [selectedAddress, clearErrors]);

  return (
    <Grid item={true} container={true} xs={12} direction="column">
      <Grid item={true}>
        <Card
          elevation={7}
          square={true}
          sx={{
            mt: 4,
            display: 'flex',
            padding: '8px',
            position: 'relative',
          }}
        >
          <CardContent>
            <Grid container={true} direction="column" spacing={2}>
              <Grid item={true}>
                {t('WIZARD.LANWANCHOISE', {
                  type: wizard.serverType === 0 ?
                    'Emby' :
                    'Jellyfin',
                },
                )}
              </Grid>
              <Grid item={true}>
                <FormControl style={{width: '100%'}}>
                  <Controller
                    name="selectedAddress"
                    control={control}
                    render={({field}) => (
                      <Select
                        sx={{
                          width: '100%',
                          marginTop: [2, 2, 2, 0, 0],
                        }}
                        variant="outlined"
                        {...field}
                      >
                        {
                          wizard.address !== wizard.mediaServerInfo.wanAddress &&
                          wizard.address !== wizard.mediaServerInfo.localAddress &&
                          <MenuItem value={wizard.address}>
                            {t('COMMON.CURRENT')}&nbsp;
                            <Box component="span" sx={{fontStyle: 'italic'}}>
                              ({wizard.address})
                            </Box>
                          </MenuItem>
                        }
                        {
                          wizard.mediaServerInfo.wanAddress !== null &&
                          <MenuItem value={wizard.mediaServerInfo.wanAddress}>
                            WAN&nbsp;
                            <Box component="span" sx={{fontStyle: 'italic'}}>
                              ({wizard.mediaServerInfo.wanAddress})
                            </Box>
                          </MenuItem>
                        }
                        <MenuItem value={wizard.mediaServerInfo.localAddress}>
                          LAN&nbsp;
                          <Box component="span" sx={{fontStyle: 'italic'}}>
                            ({wizard.mediaServerInfo.localAddress})
                          </Box>
                        </MenuItem>
                        <MenuItem value="other">{t('COMMON.OTHER')}</MenuItem>
                      </Select>
                    )}
                  />
                </FormControl>
              </Grid>
              <Grid item={true}>
                <EsTextInput
                  readonly={selectedAddress !== 'other'}
                  inputRef={addressRegister}
                  defaultValue={getValues('address')}
                  error={errors.address}
                  errorText={{
                    validate: t('SETTINGS.MEDIASERVER.NOADDRESS'),
                    protocol: 'Protocol should be http or https',
                    url: 'URL be longer then 0 characters',
                    port: 'Port should be a number between 1 and 65535',
                  }}
                  label={t('SETTINGS.MEDIASERVER.ADDRESS')}
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Grid>
      {wizard.serverType === 1 ? (
        <Grid item={true}>
          <Card
            elevation={7}
            square={true}
            sx={{
              mt: 4,
              display: 'flex',
              padding: '8px',
              position: 'relative',
            }}
          >
            <CardContent>
              <Grid container={true} direction="column" spacing={2}>
                <Grid item={true}>
                  <Typography gutterBottom={true}>
                    {t('WIZARD.JELLYFIN.ADMINTEXT')}
                  </Typography>
                </Grid>
                <Grid item={true}>
                  <Controller
                    name="selectedAdmin"
                    control={control}
                    render={({field}) => (
                      <Select
                        variant="outlined"
                        {...field}
                      >
                        {
                          wizard.administrators.map((admin) => (
                            <MenuItem value={admin.id} key={admin.id}>{t(admin.name)}</MenuItem>
                          ))
                        }
                      </Select>)}
                  />
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      ) : null}
    </Grid>
  );
});

import React, {forwardRef, useContext, useImperativeHandle} from 'react';
import {Controller, useForm} from 'react-hook-form';
import {useTranslation} from 'react-i18next';

import {
  Checkbox, FormControlLabel, Grid, MenuItem, Select, SelectChangeEvent, StepProps, Typography,
} from '@mui/material';

import i18n from '../../../i18n';
import {SettingsContext} from '../../../shared/context/settings';
import {WizardContext} from '../../../shared/context/wizard/WizardState';
import {Language} from '../../../shared/models/language';
import {ValidationHandleWithSave} from '../Interfaces';

export const Intro = forwardRef<ValidationHandleWithSave, StepProps>((props, ref) => {
  const {t} = useTranslation();
  const {setLanguage, setMonitoring} = useContext(WizardContext);
  const {languages} = useContext(SettingsContext);

  const {trigger, getValues, control, formState: {isValid}} = useForm({
    mode: 'onBlur',
    defaultValues: {
      language: 'en-US',
      enableMonitoring: false,
    },
  });

  useImperativeHandle(ref, () => ({
    async validate(): Promise<boolean> {
      await trigger();
      return Promise.resolve(isValid);
    },
    saveChanges(): void {
      if (isValid) {
        const {language, enableMonitoring} = getValues();
        setLanguage(language);
        setMonitoring(enableMonitoring);
      }
    },
  }));

  const handleLanguageChange = (event: SelectChangeEvent<string>) => {
    const lang = event.target.value;
    i18n.changeLanguage(lang);
  };

  const crowdinText = {__html: t('WIZARD.CROWDINHELP')};
  const introText = {__html: t('WIZARD.INTROTEXT')};

  return (
    <Grid container={true} direction="column" spacing={4}>
      <Grid item={true}>
        <Typography variant="h4" color="primary">
          {t('WIZARD.TITLE')}
        </Typography>
        <Typography variant="body1" dangerouslySetInnerHTML={introText} />
      </Grid>
      <Grid item={true} container={true} direction="column" spacing={1}>
        <Grid item={true}>
          <Typography variant="h6" color="primary">
            {t('WIZARD.LANGUAGE')}
          </Typography>
          <Typography>
            {t('WIZARD.SELECTLANGUAGE')}
          </Typography>
        </Grid>
        <Grid item={true}>
          <Controller
            name="language"
            control={control}
            defaultValue={getValues('language')}
            render={({field}) => (
              <Select
                className="max-width"
                variant="outlined"
                sx={{minWidth: 250}}
                {...field}
                onChange={(value) => {
                  field.onChange(value);
                  handleLanguageChange(value);
                }}
              >
                {languages.map((x: Language) => (
                  <MenuItem key={x.code} value={x.code}>
                    {x.name}
                  </MenuItem>
                ))}
              </Select>
            )}
          />
        </Grid>
        <Grid item={true}>
          <Typography
            variant="body1"
            dangerouslySetInnerHTML={crowdinText}
            sx={{
              'fontStyle': 'italic',
              'fontSize': '0.8rem',
              '& a': {
                color:
                (theme) => theme.palette.mode === 'dark' ?
                  theme.palette.secondary.light :
                  theme.palette.secondary.dark,
              },
            }}
          />
        </Grid>
      </Grid>
      <Grid item={true} container={true} direction="column" spacing={1}>
        <Grid item={true}>
          <Typography variant="h6" color="primary">
            {t('WIZARD.EXCEPTIONLOGGING')}
          </Typography>
          <Typography variant="body1">
            {t('SETTINGS.ROLLBAR.EXCEPTIONLOGGING')}
          </Typography>
        </Grid>
        <Grid item={true}>
          <Controller
            name="enableMonitoring"
            control={control}
            defaultValue={getValues('enableMonitoring')}
            render={({field}) =>
              <FormControlLabel
                control={
                  <Checkbox
                    {...field}
                    checked={getValues('enableMonitoring')}
                    disableRipple={true} color="primary" />
                }
                label={t('SETTINGS.ROLLBAR.ENABLEROLLBAR') as string}
              />
            }
          />
        </Grid>
      </Grid>
    </Grid>
  );
});

import React, {forwardRef, useContext, useImperativeHandle} from 'react';
import {Controller, useForm} from 'react-hook-form';
import {useTranslation} from 'react-i18next';

import OpenInNewIcon from '@mui/icons-material/OpenInNew';
import {
  Box, FormControl, FormHelperText, Grid, IconButton, MenuItem, Select, Stack, Typography,
} from '@mui/material';

import {StepProps, ValidationHandleWithSave} from '..';
import embyLogo from '../../../shared/assets/images/emby.png';
import jellyfinLogo from '../../../shared/assets/images/jellyfin.png';
import {EsTextInput} from '../../../shared/components/esTextInput';
import {WizardContext} from '../../../shared/context/wizard/WizardState';
import {useServerType} from '../../../shared/hooks';
import {MediaServer} from '../../../shared/models/mediaServer';

export const MediaServerDetails =
forwardRef<ValidationHandleWithSave, StepProps>((props, ref) => {
  const {wizard, setMediaServerNetworkInfo} = useContext(WizardContext);
  const {getMediaServerTypeStringFromNumber} = useServerType();
  const {t} = useTranslation();

  useImperativeHandle(ref, () => ({
    async validate(): Promise<boolean> {
      await trigger();
      return Promise.resolve(isValid);
    },
    saveChanges(): void {
      if (isValid) {
        const {address, apiKey, type} = getValues();
        const mediaServer: MediaServer = {
          address,
          apiKey,
          id: '',
          name: '',
          type,
        };
        setMediaServerNetworkInfo(mediaServer);
      }
    },
  }));

  const {register, trigger, getValues, formState: {errors, isValid}, control} = useForm({
    mode: 'onBlur',
    defaultValues: {
      address: wizard.address,
      apiKey: wizard.apiKey,
      type: wizard.serverType,
    },
  });

  const serverTypeList = [
    {id: 0, value: 0, label: 'Emby', logo: embyLogo},
    {id: 1, value: 1, label: 'Jellyfin', logo: jellyfinLogo},
  ];

  const openMediaServer = async () => {
    try {
      const {address, type} = getValues();
      const htmlSuffix = type === 1 ? '.html' : '';
      window.open(
        `${address}/web/index.html#!/apikeys${htmlSuffix}`,
        '_blank',
      );
    } catch {
      await trigger(['address']);
    }
  };

  const addressRegister = register('address', {required: true});
  const apiKeyRegister = register('apiKey', {required: true});

  return (
    <Stack spacing={3}>
      <Typography variant="h4" color="primary">
        {t('WIZARD.SERVERCONFIGURATION')}
      </Typography>
      <FormControl style={{width: '100%'}}>
        <Controller
          name="type"
          control={control}
          defaultValue={getValues('type')}
          render={({field}) => (
            <Select
              id="type"
              label={t('WIZARD.SERVERTYPELABEL')}
              className="max-width"
              variant="outlined"
              sx={{minWidth: 250}}
              {...field}
            >
              {serverTypeList.map((x) => (
                <MenuItem key={x.id} value={x.value}>
                  <Grid container={true} spacing={1} alignItems="center">
                    <Grid item={true}>
                      <img src={x.logo} height={20} alt="mediaserver logo" />
                    </Grid>
                    <Grid item={true}>
                      {x.label}
                    </Grid>
                  </Grid>
                </MenuItem>
              ))}
            </Select>
          )}
        />
        <FormHelperText>{t('WIZARD.SERVERTYPEGHELPER')}</FormHelperText>
      </FormControl>
      <Box display="flex">
        <Box sx={{flex: 1}}>
          <EsTextInput
            inputRef={addressRegister}
            defaultValue={getValues('address')}
            error={errors.address}
            label={t('SETTINGS.MEDIASERVER.ADDRESS')}
            errorText={{required: t('SETTINGS.MEDIASERVER.NOADDRESS')}}
            helperText={t('SETTINGS.MEDIASERVER.ADDRESSHINT')}
          />
        </Box>
        <Box sx={{flex: 0}}>
          <IconButton color="primary" component="span" onClick={openMediaServer} sx={{ml: 0.5}}>
            <OpenInNewIcon/>
          </IconButton>
        </Box>
      </Box>
      <Box>
        <EsTextInput
          inputRef={apiKeyRegister}
          defaultValue={getValues('apiKey')}
          error={errors.apiKey}
          label={t('SETTINGS.MEDIASERVER.APIKEY')}
          errorText={{required: t('SETTINGS.MEDIASERVER.NOAPIKEY')}}
          helperText={
            t('SETTINGS.MEDIASERVER.APIKEYHINT',
              {type: getMediaServerTypeStringFromNumber(getValues('type'))},
            )
          }
        />
      </Box>
    </Stack>
  );
});

