import React, {forwardRef, useContext, useEffect} from 'react';
import {useTranslation} from 'react-i18next';

import {Grid, Stack, Typography} from '@mui/material';

import {EsLoading} from '../../../shared/components/esLoading';
import {WizardContext} from '../../../shared/context/wizard/WizardState';
import {MediaServer, MediaServerUdpBroadcast} from '../../../shared/models/mediaServer';
import {StepProps, ValidationHandleWithSave} from '../Interfaces';
import {NewServerCard, ServerCard} from './Helpers';

export const SearchMediaServer =
forwardRef<ValidationHandleWithSave, StepProps>((props: StepProps, ref) => {
  // eslint-disable-next-line react/prop-types
  const {handleNext} = props;
  const {t} = useTranslation();
  const {wizard, getMediaServers, mediaServersLoading, setMediaServerNetworkInfo} = useContext(WizardContext);

  useEffect(() => {
    getMediaServers();
  }, []);

  const onServerCardClick = async (server: MediaServerUdpBroadcast) => {
    const url = `http${server.protocol === 0 ? 's' : ''}://${server.address}:${server.port}${server.baseUrl ?? ''}`;
    const mediaServer: MediaServer = {
      address: url,
      apiKey: '',
      type: server.type,
      name: server.name,
      id: server.id,
    };

    setMediaServerNetworkInfo(mediaServer);
    if (handleNext !== undefined) {
      await handleNext();
    }
  };

  const addNewServer = async () => {
    const mediaServer: MediaServer = {
      address: '',
      apiKey: '',
      type: 0,
      name: '',
      id: '',
    };

    setMediaServerNetworkInfo(mediaServer);
    if (handleNext !== undefined) {
      await handleNext();
    }
  };

  return (
    <Stack>
      <Typography variant="h4" color="primary">
        {t('WIZARD.SERVERCONFIGURATION')}
      </Typography>
      <EsLoading
        loading={mediaServersLoading}
        label={t('WIZARD.SEARCHSERVERS')}
        width='100%'
        height='200px'
      >
        <Grid container={true} direction="column" sx={{minHeigt: 115}}>
          <Typography variant="body1" sx={{mb: 3}}>
            {wizard.foundServers.length > 0 ? t('WIZARD.FOUNDTEXT') : t('WIZARD.NOTFOUNDTEXT')}
          </Typography>
          <Grid item={true} container={true} direction="row" xs={12} spacing={2}>
            {wizard.foundServers.map((x: MediaServerUdpBroadcast) => (
              <ServerCard server={x} key={x.id} onClick={onServerCardClick} />
            ))}
            <NewServerCard onClick={addNewServer} />
          </Grid>
        </Grid>
      </EsLoading>
    </Stack>
  );
});

import React, {
  forwardRef, useContext, useEffect, useImperativeHandle, useRef, useState,
} from 'react';
import {useTranslation} from 'react-i18next';

import {Grid, Typography} from '@mui/material';

import {EsLoading} from '../../../shared/components/esLoading';
import {WizardContext} from '../../../shared/context/wizard/WizardState';
import {MediaServerLogin} from '../../../shared/models/mediaServer';
import {getAdministrators, getLibraries, getServerInfo, testApiKey} from '../../../shared/services';
import {StepProps, ValidationHandleWithSave} from '../Interfaces';
import {TestFailed, TestSuccessFul} from './Helpers';

export const TestMediaServer =
forwardRef<ValidationHandleWithSave, StepProps>((props, ref) => {
  const {t} = useTranslation();
  const {wizard, setMediaServerInfo,
    setAdministrators} = useContext(WizardContext);

  const [loadingLabel, setLoadingLabel] = useState('WIZARD.STEPONE');
  const [isLoading, setIsLoading] = useState(true);
  const [currentStep, setCurrentStep] = useState(1);
  const [inError, setInError] = useState(false);

  const successRef = useRef<React.ElementRef<typeof TestSuccessFul>>(null);

  useImperativeHandle(ref, () => ({
    async validate(): Promise<boolean> {
      return await successRef.current?.validate() ?? Promise.resolve(false);
    },
    saveChanges(): void {
      successRef.current?.saveChanges();
    },
  }));

  useEffect(() => {
    const performSteps = async () => {
      if (currentStep === 1) {
        const result = await testApiKey({
          address: wizard.address,
          apiKey: wizard.apiKey,
          type: wizard.serverType,
        } as MediaServerLogin);

        if (result == null) {
          setIsLoading(false);
          return;
        }

        if (result) {
          setCurrentStep(2);
          setLoadingLabel('WIZARD.STEPTWO');
          const serverInfo = await getServerInfo(true);
          if (serverInfo == null) {
            setIsLoading(false);
            return;
          }

          setCurrentStep(3);
          setLoadingLabel('WIZARD.STEPTHREE');
          const admins = await getAdministrators();
          if (admins == []) {
            setIsLoading(false);
            return;
          }

          setCurrentStep(4);
          setLoadingLabel('WIZARD.STEPFOUR');
          const libs = await getLibraries();
          if (libs == []) {
            setIsLoading(false);
            return;
          }
          setMediaServerInfo(serverInfo);
          setAdministrators(admins);
          setIsLoading(false);
        } else {
          setInError(true);
          setIsLoading(false);
        }
      }
    };
    performSteps();
  }, [currentStep, wizard, isLoading, setMediaServerInfo, setAdministrators]);

  return (
    <Grid
      container={true}
      direction="column"
      spacing={2}
    >
      <Grid item={true}>
        <Typography variant="h4" color="primary">
          {t('WIZARD.SERVERCONFIGURATIONTEST')}
        </Typography>
      </Grid>
      <Grid item={true}>
        <EsLoading
          width='100%'
          height='200px'
          loading={isLoading}
          label={t(loadingLabel, {step: currentStep, total: 4})}
        >
          {!inError ? <TestSuccessFul ref={successRef} /> : <TestFailed />}
        </EsLoading>
      </Grid>
    </Grid>
  );
});

import React, {forwardRef, useContext, useImperativeHandle} from 'react';
import {useForm} from 'react-hook-form';
import {useTranslation} from 'react-i18next';

import {Box, Stack, Typography} from '@mui/material';

import {EsTextInput} from '../../../shared/components/esTextInput';
import {WizardContext} from '../../../shared/context/wizard/WizardState';
import {StepProps, ValidationHandleWithSave} from '../Interfaces';

export const UserDetails = forwardRef<ValidationHandleWithSave, StepProps>( (props, ref) => {
  const {t} = useTranslation();
  const {wizard, setUserDetails} = useContext(WizardContext);

  const {register, trigger, getValues, formState: {errors, isValid}} = useForm({
    mode: 'onBlur',
    defaultValues: {
      username: wizard.username,
      password: wizard.password,
    },
  });

  useImperativeHandle(ref, () => ({
    async validate(): Promise<boolean> {
      await trigger();
      return Promise.resolve(isValid);
    },
    saveChanges(): void {
      if (isValid) {
        const {username, password} = getValues();
        setUserDetails(username, password);
      }
    },
  }));

  const usernameRegister = register('username', {required: true});
  const passwordRegister = register('password', {required: true, minLength: 6});

  return (
    <Stack spacing={4}>
      <Typography variant="h4" color="primary">
        {t('WIZARD.ACCOUNT')}
      </Typography>
      <Typography variant="body1">
        {t('WIZARD.USERDETAILTEXT')}
      </Typography>
      <Box>
        <Box sx={{maxWidth: 250}}>
          <EsTextInput
            inputRef={usernameRegister}
            label={t('SETTINGS.ACCOUNT.USERNAME')}
            error={errors.username}
            defaultValue={getValues('username')}
            errorText={{
              required: t('SETTINGS.ACCOUNT.NOUSERNAME'),
            }}
          />
        </Box>
        <Box sx={{maxWidth: 250}}>
          <EsTextInput
            inputRef={passwordRegister}
            label={t('SETTINGS.PASSWORD.PASSWORD')}
            error={errors.password}
            type="password"
            defaultValue={getValues('password')}
            errorText={{
              required: t('SETTINGS.PASSWORD.NOPASSWORD'),
              minLength: t('SETTINGS.PASSWORD.PASSWORDMINLENGTH'),
            }}
          />
        </Box>
      </Box>
    </Stack>
  );
});
